import React from 'react'
import { useParams } from 'react-router-dom';
const MovieView = (props) => {
  const { id } = useParams();
  const movie = props.movies.find(m => m.id === Number(id));
  return (
    <div>
        <video src={movie.link} > </video>
        <p>{movie.description}</p>
    </div>
  )
}

export default MovieView
